import React, { Component } from 'react';


class Questions extends Component {


    state = {
        q : {
            "What is React ?" : {"Framework" : 0, "Libarary" : 1, "ProgrammingLanguage" : 0}, 
            "What is Angular ?" : {"Framework" : 1, "Libarary" : 0, "ProgrammingLanguage" : 0},
        },
        current_question: '', 
        score : 0,
        answered: '',
    }

    componentDidMount(){

        const qq = this.state.q[0];
        this.setState({current_question:qq});
    }

    GetAnswer = (a) => {
        if(a == 0) {
            return <p>Wrong</p>
        }
        else {
            return <p>Correct</p>
        }

    }

    Toggle = (a, b) => {
        this.setState({answered: b});
        this[a].style.display ="inline";
        const scoreholder = this.state.score;
        if(this[a].textContent == "Correct") {
            this.setState({score: scoreholder+1});
            this[a].style.color ="green";
        }


    }


    render ()
    {
        console.log("zbiii")
        console.log(this.state.current_question)
        const questions = Object.keys(this.state.q);
        return (
            <div>{questions.map(i => 
            <div>
                <p>{i}</p>
                <ul>{Object.keys(this.state.q[i])
                    .map(j => <div>{this.state.answered == i ? <p>{j}</p> : <p onClick={() => this.Toggle(i + j, i)}>{j}</p> }
                        <p ref={a => { this[i + j] = a }} style={{color: "red", display: "none"}}>{this.GetAnswer(this.state.q[i][j])}</p>
                        </div>)}
                </ul>
            </div>)}
            <p style={{marginLeft: 600}}>Your Score Is : {this.state.score}</p>
            </div>
        );
    }

}

export default Questions;
