import React, { Component } from 'react';


class Questions extends Component {


    state = {
        q : {
            "What is React ?" : {"Framework" : 0, "Libarary" : 1, "ProgrammingLanguage" : 0}, 
            "What is Angular ?" : {"Framework" : 1, "Libarary" : 0, "ProgrammingLanguage" : 0},
        },
        score : 0,
        answered: '',
    }

    GetAnswer = (a) => {
        if(a == 0) {
            return <p>Wrong</p>
        }
        else {
            return <p>Correct</p>
        }

    }

    Toggle = (a) => {
        this[a].style.display ="inline";
        const scoreholder = this.state.score;
        if(this[a].textContent == "Correct") {
            this.setState({score: scoreholder+1});
        }

    }


    render ()
    {
        const questions = Object.keys(this.state.q);
        return (
            <div>{questions.map(i => 
            <div>
                <p>{i}</p>
                <ul>{Object.keys(this.state.q[i])
                    .map(j => <div>
                        <p onClick={() => this.Toggle(j + "   " + i)}>{j}</p>
                        <p ref={a => { this[j + "   " + i] = a }} style={{color: "red", display: "none"}}>{this.GetAnswer(this.state.q[i][j])}</p>
                        </div>)}
                </ul>
            </div>)}
            </div>
        );
    }

}

export default Questions;
