import React, { Component } from 'react';
import './QuizzApp.css'


class Questions extends Component {


    state = {
        q : {
            0 : {"What is React ?" : {"Framework" : 0, "Libarary" : 1, "ProgrammingLanguage" : 0}}, 
            1 : {"What is Angular ?" : {"Framework" : 1, "Libarary" : 0, "ProgrammingLanguage" : 0}},
            2 : {"How Trash is Kellout's Vision ?" : {"Very Trash" : 1, "Mildely Trash" : 0, "Not Trash" : 0}},
        },
        current_question: 0, 
        score : 0,
        answered: '',
        extended: 1,
    }

    componentDidUpdate() {
        if(this.state.extended == 0){
            this.setState({extended: 1})
        }
    }




    GetAnswer = (a) => {
        if(a == 0) {
            return <p>Wrong</p>
        }
        else {
            return <p>Correct</p>
        }

    }

    Next = () => {
        let current = this.state.current_question+1;
        if(current < Object.keys(this.state.q).length) {
        this.setState({current_question: current, extended: 0});
        }

    }

    Toggle = (a, b) => {
        this.setState({answered: b});
        this[a].style.display ="inline";
        const scoreholder = this.state.score;
        if(this[a].textContent == "Correct") {
            this.setState({score: scoreholder+1});
            this[a].style.color ="green";
        }


    }



    render ()
    {

        const arr = Object.keys(this.state.q[this.state.current_question]);
        const holder =  this.state.q[this.state.current_question];
        const answers = holder[arr[0]]


        return (
            <div>
            <div>
                <p>{arr[0]}</p>
                <ul>{Object.keys(answers)
                    .map(j => <div>{this.state.answered == arr[0] ? <p>{j}</p> : <p onClick={() => this.Toggle(arr[0] + j, arr[0])}>{j}</p> }
                        {this.state.extended == 1 ? <p ref={a => { this[arr[0] + j] = a }} style={{color: "red", display: "none"}}>{this.GetAnswer(answers[j])}</p> : <p></p>}
                        </div>)}
                </ul>
            </div>
            <p style={{marginLeft: 600}}>Your Score Is : {this.state.score}</p>
            {this.state.answered == arr[0] ? <button onClick={this.Next} style={{marginLeft: 630}}>Next</button> : <button style={{marginLeft: 630}}>Next</button>}
            </div>
        );
    } 

}

export default Questions;
