import React, { Component } from 'react';


class Questions extends Component {

    state = {
        q : {
            "What is React ?" : {"Framework" : 0, "Libarary" : 1, "ProgrammingLanguage" : 0}, 
            "What is Angular ?" : {"Framework" : 1, "Libarary" : 0, "ProgrammingLanguage" : 0},
        },
    }

    GetAnswer = (a) => {
        if(a == 0) {
            return 0;
        }
        else {
            return 1;
        }

    }

    display = () => {
        if(this.GetAnswer == 0) {
            return <p>Wrong</p>
        } 
        else {
            if(this.GetAnswer == 1) {
                return <p>Correct</p>
        }
            else {return <p></p>}
    }

    }

    render ()
    {
        const questions = Object.keys(this.state.q);
        return (
            <div>{questions.map(i => 
            <div>
                <p>{i}</p>
                <ul>{Object.keys(this.state.q[i]).map(j => <p onClick={() => this.GetAnswer(this.state.q[i][j])}>{j}</p>)}</ul>
                <p>{this.display}</p>
            </div>)}
            </div>
        );
    }

}

export default Questions;
